mapper.select:

		select rownum as rnum, A.*
		from ( select rownum as rnum, B.*
       			from ( select * from music order by m_goodpoint desc ) B   
       			where rownum <= #{endNo}
      			) A
		where rnum >= #{startNo}


		select rownum as rnum, A.*
		from ( select rownum as rnum, B.*
       			from ( select * from music where memberidx = #{memberidx} order by m_goodpoint desc ) B   
       			where rownum <= #{endNo}
      			) A
		where rnum >= #{startNo}
